mapper.select:
SELECT t_a.appraisal_id AS appraisalId, t_a.title AS title, t_a.content AS content, t_a.create_by AS createBy, t_cr.nick_name AS creatorName, t_cr.portrait, DATE_FORMAT( t_a.create_time, '%Y-%m-%d %H:%i:%s' ) AS createTime, COUNT(DISTINCT t_aa.user_id) AS appreciateCount, COUNT(DISTINCT t_af.user_id) AS favoriteCount, SUBSTRING_INDEX(GROUP_CONCAT(DISTINCT t_ai.img_url ORDER BY t_ai.pk_id ASC),',',1) AS img, GROUP_CONCAT(DISTINCT t_at.tag) AS tag, GROUP_CONCAT(DISTINCT t_c.commodity_name) AS commodityName, GROUP_CONCAT(DISTINCT t_b.brand_name) AS brandName FROM zc_appraisal t_a LEFT JOIN zc_appraisal_appreciate t_aa ON t_a.appraisal_id = t_aa.appraisal_id AND t_aa.is_vaild = 1 LEFT JOIN zc_appraisal_favorite t_af ON t_a.appraisal_id = t_af.appraisal_id AND t_af.is_vaild = 1 LEFT JOIN zc_appraisal_tag t_at ON t_a.appraisal_id = t_at.appraisal_id AND t_at.is_vaild = 1 LEFT JOIN zc_appraisal_img t_ai ON t_a.appraisal_id = t_ai.appraisal_id LEFT JOIN cms_user t_cr ON t_a.create_by = t_cr.user_id LEFT JOIN zc_appraisal_commodity t_ac ON t_a.appraisal_id = t_ac.appraisal_id AND t_ac.is_vaild = 1 LEFT JOIN zc_commodity t_c ON t_ac.commodity_id = t_c.commodity_id LEFT JOIN zc_brand t_b ON t_c.brand_id = t_b.brand_id WHERE t_a.is_valid = 1
AND t_b.brand_id = #{brandId,jdbcType=VARCHAR}
AND t_c.commodity_id =#{commodityId,jdbcType=VARCHAR}
AND ( t_a.title LIKE CONCAT('%',#{search,jdbcType=VARCHAR},'%') OR t_at.tag = #{search,jdbcType=VARCHAR} OR t_c.commodity_name= #{search,jdbcType=VARCHAR} OR t_b.brand_name = #{search,jdbcType=VARCHAR} )
GROUP BY t_a.appraisal_id
